pragma solidity >=0.4.21 <0.6.0;

contract MMEToken {
  mapping(address => bool) bills;
  mapping(address => uint) amounts;
  constructor() public {}

  // To be done in Bill contract
  function hasBill(address who) public view returns (bool) {
    return bills[who];
  }
  function newBill(address who, uint amount) public {
    bills[who] = true;
    amounts[who] = amount;
  }
  function removeBill(address who) public {
    bills[who] = false;
    amounts[who] = 0;
  }
  function getAmount(address who) public view returns (uint) {
    return amounts[who];
  }

  // To be done in TOken contract
  function getTokenAmount(address who) public view returns (uint) {
    return amounts[who]; // random
  }

  function payBill(uint tokensToFond) public payable returns (bool) {
    require(bills[msg.sender]);
    require(msg.value > amounts[msg.sender]);
    amounts[msg.sender] = tokensToFond; // random to suppress warning
    bills[msg.sender] = false; // random stuff here
    // TODO: send tokensToFond to Fond
    // TODO: make payable
    //this.removeBill(who);
    return true;
  }
}
